/**
 * The line style of the series.
 *
 * @example
 * ```jsx
 * import {
 *  Chart,
 *  ChartSeries,
 *  ChartSeriesItem
 * } from '@progress/kendo-react-charts';
 *
 * const data = [1, 2, 3];
 *
 * const ChartContainer = () => (
 *  <Chart>
 *    <ChartSeries>
 *      <ChartSeriesItem type="line" data={data} style="smooth" />
 *    </ChartSeries>
 *  </Chart>
 * );
 *
 * ReactDOM.render(
 *  <ChartContainer />,
 *  document.querySelector('my-app')
 * );
 * ```
 */
export type LineStyle = 'normal' | 'step' | 'smooth';
